import './App.css';
import {useState} from "react";
import Axios from 'axios';

function App() {

  const [email, setEmail] = useState("");
  const [buttonColor, setButtonColor] = useState("");

  const addEmail = () => {
    if(email.trim() === "") {
      console.log("email field is empty");
      return;
    }

    Axios.post("http://localhost:3001/send", {
      email: email
    }).then(() => {
      console.log("Success");
      setButtonColor('green');
    }).catch(error => {
      console.log("Error caught: ", error);
    })
  }

  return (
    <div className="App">
      <div className='container'>
        <div className='loginContainer'>

          <input className="login" placeholder="admin password" />
          <button className="loginButton">Login</button>

        </div>
      </div>
      <h1>Email Retriever</h1>
      <h3>Sub Heading</h3>
      <image alt="emailImg"></image>
      <input type="text" onChange={(event) => {
        setEmail(event.target.value);
      }} placeholder="Enter email here" /><br />
      <button style={{backgroundColor: buttonColor}} onClick={addEmail}>Submit</button>
      <div className="listContainer">
        <div className="listItems">
          <ul>
            <li>list item 1</li>
            <li>list item 2</li>
            <li>list item 3</li>
          </ul>
        </div>
      </div>

      <div className="adminContainer">
        <label style={{fontSize: 10, fontWeight: "bold"}}>admin panel</label>

        <div className="showButtonContainer">
          <button className="showEmailsButton">Show Emails</button>
        </div>

      </div>

    </div>
  );
}

export default App;
